mapper.update:
    UPDATE seeot_board
    SET	member_name = #{memberName},
    	board_title = #{boardTitle},
    	board_content = #{boardContent},
    	board_qnatype = #{boardQnAType}
	WHERE board_no = #{boardNo}

mapper.select:
SELECT * FROM seeot_reply
WHERE board_no = #{boardNo}
ORDER BY reply_date ASC
